import React from "react";

import styles from "./landingSection.module.css";
import BigCard from "../bigCard/bigCard";
import SmallCard from "../smallCard/smallCard";

const LandingSection = ({ imageUrl, children }) => {
  const landingSectionStyles = {
    position: "absolute",
    backgroundImage: `url(${imageUrl})`,
    background: "#00000067 0 % 0 % no-repeat padding-box",
    backgroundColor: "rgba(0, 0, 0, 0.4)",
    height: "100vh",
    width: "100vw",
    filter: "blur(50px)",
    backgroundPosition: "center",
    backgroundRepeat: "no-repeat",
    backgroundSize: "cover"
  };
  return (
    <div style={{ position: "relative" }}>
      <div style={landingSectionStyles}>
        {/* <div className={styles.landingCover}></div> */}
      </div>
      <div className={styles.landingSection}>
        <div className={styles.bigSection}>
          <h1>Hello</h1>
          <BigCard
            imageUrl={imageUrl}
            name="Terminator:dark fate"
            rating="7.6"
            restriction="16+"
            genre="action, sci-fi"
          />
        </div>
        <div className={styles.smallSection}>
          <h1>Hello</h1>
          <SmallCard name="the king" imageUrl={imageUrl} />
        </div>
      </div>
    </div>
  );
};

export default LandingSection;
