import React from "react";

import styles from "./bigCard.module.css";

import BigCardMovieInfo from "../bigCardMovieInfo/bigCardMovieInfo";

const BigCard = ({ imageUrl = null, name, rating, restriction, genre }) => {
  const bigCardStyles = {
    position: "relative",
    backgroundImage: "url(" + imageUrl + ")",
    height: "100%",
    width: "100%",
    textAlign: "left"
  };
  return (
    <div styles={bigCardStyles}>
      <img className={styles.moviePoster} src={imageUrl} alt="Movie poster" />
      <BigCardMovieInfo
        name={name}
        rating={rating}
        restriction={restriction}
        genre={genre}
      >
        <h1 className={styles.lastMovieText}>LATEST MOVIE</h1>
      </BigCardMovieInfo>
    </div>
  );
};

export default BigCard;
